mapper.insert:
INSERT INTO user (
	userid, username, pwd, hp, email, addr, addr2, birth, gender
) VALUES (
	#{userid}, #{username}, MD5(#{pwd}), #{hp}, 
	#{email}, #{addr}, #{addr2}, #{birth}, #{gender}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE user
pwd = MD5(#{pwd}),
pwd = MD5(#{pwd}), hp = #{hp}, addr = #{addr}, addr2 = #{addr2}
WHERE usernum = #{usernum}
